mapper.update:
UPDATE koala_user
SET user_age = #{userAge,jdbcType=NUMERIC},user_phone = #{userPhone,jdbcType=VARCHAR}, user_email = #{userEmail,jdbcType=VARCHAR}
,user_true_name = #{userTrueName,jdbcType=VARCHAR}, user_sex = #{userSex,jdbcType=VARCHAR}
, last_update_time = #{lastUpdateTime,jdbcType=DATE},last_login_time=#{lastLoginTime,jdbcType=DATE}
where user_name = #{userName,jdbcType=VARCHAR}
